        @autobind
        handleInsertNodePaste(){

          return new Promise(async (resolve,reject)=>{

            let nodeText = this.state.pasteText; //WINDOW.prompt('Node JSON String','');
            if(!nodeText){
              return false;
            }

            let node = JSON.parse(nodeText);
            console.log('Node input:', node);

            // Create this new one, then iterate down the tree of kids 
            // - if this one has a nodeId, ask if we want to keep it? 
            let confirmed = WINDOW.prompt('Save node ('+node.type+') and children for sure?','y');
            if(confirmed != 'y'){
              return false;
            }

            // iterate
            // - save this one, then start saving down tree
            let newNode = {
              nodeId: null, 
              type: node.type,
              data: node.data,
            }

            console.log('newNode:', newNode);

            let savedNode = await this.forceNewNode(newNode, {skipExistingLearnedNodeId: true});

            console.log('Saved Node');

            const saveChildNodes = (nodeId, childNodes) => {
              return new Promise(async (resolve, reject)=>{

                console.log('Saving children');

                for(let tmpNode of childNodes){
                  let newChildNode = {
                    nodeId,
                    type: tmpNode.type,
                    data: tmpNode.data,
                  }
                  let savedChildNode = await this.forceNewNode(newChildNode, {skipExistingLearnedNodeId: true});

                  if(tmpNode.nodes && tmpNode.nodes.length){

                    await saveChildNodes(savedChildNode.data._id, tmpNode.nodes);

                  }
                }
                resolve();
              });
            }

            await saveChildNodes(savedNode.data._id, node.nodes);

            console.log('Done Adding via Paste');

            try {
              universe.EE.emit('nodes-modified', '');
            }catch(err){
              console.error(err);
            }

          });
        }

